import React, { useState } from "react";
import axios from "axios";

const Engineeringsuggest = () => {
  const [formData, setFormData] = useState({
    name: "",
    location: "",
    education: "",
    fatherName: "",
    motherName: "",
    siblings: "",
    resume: null,
  });

  const [aiQuestion, setAiQuestion] = useState("");
  const [loading, setLoading] = useState(false);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleFileChange = (e) => {
    setFormData({ ...formData, resume: e.target.files[0] });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    try {
      const formDataToSend = new FormData();
      Object.entries(formData).forEach(([key, value]) => {
        formDataToSend.append(key, value);
      });

      const response = await axios.post("http://localhost:5000/api/engineering-question", formDataToSend);
      setAiQuestion(response.data.question);
    } catch (error) {
      console.error("Error sending data to GPT API:", error);
    }
    setLoading(false);
  };

  return (
    <div className="bg-white shadow-lg rounded-2xl p-6 max-w-2xl mx-auto mt-10">
      <h2 className="text-2xl font-bold mb-4">Engineering Interview Setup</h2>
      <form onSubmit={handleSubmit} className="grid grid-cols-1 gap-4">
        <input name="name" placeholder="Your Name" className="input" onChange={handleChange} />
        <input name="location" placeholder="Location" className="input" onChange={handleChange} />
        <input name="education" placeholder="Education" className="input" onChange={handleChange} />
        <input name="fatherName" placeholder="Father's Name" className="input" onChange={handleChange} />
        <input name="motherName" placeholder="Mother's Name" className="input" onChange={handleChange} />
        <input name="siblings" placeholder="No. of Siblings" className="input" onChange={handleChange} />

        <div>
          <label className="block mb-1">Upload Resume</label>
          <input type="file" accept=".pdf,.doc,.docx" onChange={handleFileChange} />
        </div>

        <button type="submit" className="bg-blue-600 text-white px-4 py-2 rounded mt-4 hover:bg-blue-700">
          {loading ? "Generating..." : "Submit & Generate AI Question"}
        </button>
      </form>

      {aiQuestion && (
        <div className="mt-6 p-4 bg-green-100 rounded-md">
          <h3 className="font-semibold text-lg">ðŸ¤– AI Generated Question:</h3>
          <p>{aiQuestion}</p>
        </div>
      )}
    </div>
  );
};

export default Engineeringsuggest;
