import React, { useState, useEffect } from "react";
import { auth,db } from "../firebase";
import { doc, setDoc,getDoc } from "firebase/firestore";
import "../Loginstyle.css"
import VantaBackgroundWrapper from "../VantaBackgroundWrapper";
import {
  createUserWithEmailAndPassword,
  signInWithEmailAndPassword,
  sendEmailVerification,
  signOut
} from "firebase/auth";
 
import GoogleLogin from "./GoogleLogin";
import GithubLogin from "./GithubLogin";

const Login=()=> {
  const [isLogin, setIsLogin] = useState(false);
  const [signupData, setSignupData] = useState({ email: "", password: "" });
const [loginData, setLoginData] = useState({ email: "", password: "" });
  const [message, setMessage] = useState("");
useEffect(() => {
  if (message) {
    alert(message);
    setMessage(""); 
  }
}, [message]);
  

  const handleSignup = async () => {
    if (!signupData.email || !signupData.password) {
    setMessage(" Email and password are required for signup.");
    return;
  }
    try {
      const userCredential=await createUserWithEmailAndPassword(auth, signupData.email, signupData.password);
      const user = userCredential.user;
      await sendEmailVerification(user);

      await signOut(auth);
setMessage("Verify your Email");
   
    await user.reload(); 
if (user.emailVerified) {
  alert("✅ Email verified. You can now log in.");
} else {
  alert("❌ Email not verified yet. Please check your inbox.");
  await signOut(auth);
}
    
   
    } catch (error) {
      if (error.code === "auth/email-already-in-use") {
      setMessage(" Email already in use. Please login instead.");
    } else {
      setMessage(` ${error.message}`);
    }
    }
  };

  const handleLogin = async () => {
  if (!loginData.email || !loginData.password) {
    setMessage("Email and password are required for login.");
    return;
  }

  try {
    const userCredential = await signInWithEmailAndPassword(auth, loginData.email, loginData.password);
    const user = userCredential.user;

    if (!user.emailVerified) {
      await sendEmailVerification(user); 
      await signOut(auth);
      setMessage(" Please verify your email first. A new link has been sent.");
      return;
    }

   
    const userRef = doc(db, "users", user.uid);
    const userSnap = await getDoc(userRef);

    if (!userSnap.exists()) {
      await setDoc(userRef, {
        email: user.email,
        createdAt: new Date()
      });
    }

    setMessage("✅ Login successful!");
  } catch (error) {
    setMessage(`❌ ${error.message}`);
  }
};


  return (
    <><VantaBackgroundWrapper>
    <div className="form-structor">
      
      <div className={`signup ${isLogin ? "slide-up" : ""}`}>
        <h2 className="form-title" onClick={() => setIsLogin(false)}>
          <span>or</span>Sign up
        </h2>
        <div className="form-holder">
          
          <input
            type="email"
            className="input"
            placeholder="Email"
            name="email"
            value={signupData.email}
            onChange={(e) => setSignupData({ ...signupData, email: e.target.value })}
          />
          <input
            type="password"
            className="input"
            placeholder="Password"
            name="password"
            value={signupData.password}
            onChange={(e) => setSignupData({ ...signupData, password: e.target.value })}
          />
        </div>
        <button className="submit-btn" onClick={handleSignup}>
          Sign up
        </button>
      </div>

      
      <div className={`login ${isLogin ? "" : "slide-up"}`}>
        <div className="center">
          <h2 className="form-title" onClick={() => setIsLogin(true)}>
            <span>or</span>Log in
          </h2>
          
          <div className="form-holder">
            <div className="d-flex justify-content-center align-items-center mt-2">
  <div>
    <GoogleLogin />
    <GithubLogin />
  </div>
</div>
            <div className="d-flex justify-content-center">
  <h4>or</h4>
</div>
            <input
              type="email"
              className="input"
              placeholder="Email"
              name="email"
              value={loginData.email}
              onChange={(e) => setLoginData({ ...loginData, email: e.target.value })}
            />
            <input
              type="password"
              className="input"
              placeholder="Password"
              name="password"
              value={loginData.password}
               onChange={(e) => setLoginData({ ...loginData, password: e.target.value })}
            />
          </div>
          <button className="submit-btn text-white " onClick={handleLogin}>
            Log in
          </button>
        </div>
      </div>

     
     
    </div></VantaBackgroundWrapper></>
  );
}
export default Login;
