// src/components/EngineeringForm.js
import React, { useState, useEffect, useRef } from "react";
import axios from "axios";
import SpeechRecognition, { useSpeechRecognition } from "react-speech-recognition";
import { v4 as uuidv4 } from "uuid";
import * as webgazer from "webgazer";

const Engineeringsuggest= () => {
  const [formData, setFormData] = useState({
    name: "",
    location: "",
    education: "",
    fatherName: "",
    motherName: "",
    siblings: "",
    resume: null,
  });

  const [aiQuestion, setAiQuestion] = useState("");
  const [loading, setLoading] = useState(false);
  const [analysis, setAnalysis] = useState(null);
  const [questionNumber, setQuestionNumber] = useState(1);
  const [questionHistory, setQuestionHistory] = useState(new Set());
  const videoRef = useRef(null);

  const {
    transcript,
    listening,
    resetTranscript,
    browserSupportsSpeechRecognition,
  } = useSpeechRecognition();

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleFileChange = (e) => {
    setFormData({ ...formData, resume: e.target.files[0] });
  };

  const fetchUniqueQuestion = async (data) => {
    const maxRetries = 5;
    for (let i = 0; i < maxRetries; i++) {
      const response = await axios.post("http://localhost:5000/api/engineering-question", data);
      const newQuestion = response.data.question;
      if (!questionHistory.has(newQuestion)) {
        setQuestionHistory((prev) => new Set(prev).add(newQuestion));
        return newQuestion;
      }
    }
    return "Unable to generate a unique question. Please try again later.";
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    try {
      const formDataToSend = new FormData();
      Object.entries(formData).forEach(([key, value]) => {
        formDataToSend.append(key, value);
      });
      const question = await fetchUniqueQuestion(formDataToSend);
      setAiQuestion(question);
      resetTranscript();
      setAnalysis(null);
    } catch (error) {
      console.error("Error sending data to GPT API:", error);
    }
    setLoading(false);
  };

  const startListening = () => {
    resetTranscript();
    SpeechRecognition.startListening({ continuous: true });
  };

  const stopListening = () => {
    SpeechRecognition.stopListening();
  };

  const analyzeTranscript = async () => {
    try {
      const response = await axios.post("http://localhost:5000/api/analyze-answer", { text: transcript });
      setAnalysis(response.data);
    } catch (error) {
      console.error("Error analyzing transcript:", error);
    }
  };

  useEffect(() => {
    if (!listening && transcript) {
      analyzeTranscript();
    }
  }, [listening]);

  const handleNextQuestion = async () => {
    setLoading(true);
    try {
      const question = await fetchUniqueQuestion(formData);
      setAiQuestion(question);
      resetTranscript();
      setAnalysis(null);
      setQuestionNumber((prev) => prev + 1);
    } catch (error) {
      console.error("Error generating next question:", error);
    }
    setLoading(false);
  };

  const startCamera = async () => {
    try {
      const stream = await navigator.mediaDevices.getUserMedia({ video: true });
      if (videoRef.current) {
        videoRef.current.srcObject = stream;
      }
    } catch (err) {
      console.error("Camera access denied or not available", err);
    }
  };

  const startEyeTracking = () => {
    webgazer.setGazeListener((data, elapsedTime) => {
      if (data) {
        console.log("Gaze at:", data.x, data.y);
      }
    }).begin();

    webgazer.showVideo(false).showFaceOverlay(false).showFaceFeedbackBox(false);
  };

  useEffect(() => {
    startCamera();
    startEyeTracking();
  }, []);

  if (!browserSupportsSpeechRecognition) {
    return <span>Your browser does not support speech recognition.</span>;
  }

  return (
    <div className="container mt-5">
      <div className="card shadow-lg rounded-3">
        <div className="card-body"></div>
          <h2 className="card-title mb-4">Engineering Interview Setup</h2>

          {/* 👁️ Eye-Tracking Camera Feed */}
          <div className="mb-4">
            <h4 className="h6 fw-bold">📷 Eye Tracking Camera (Live)</h4>
            <video ref={videoRef} autoPlay playsInline className="w-100 rounded border" style={{ maxHeight: "220px" }} />
          </div>

          <form onSubmit={handleSubmit} className="row g-3">
            <div className="col-12 col-md-6">
              <input name="name" placeholder="Your Name" className="form-control" onChange={handleChange} />
            </div>
            <div className="col-12 col-md-6">
              <input name="location" placeholder="Location" className="form-control" onChange={handleChange} />
            </div>
            <div className="col-12 col-md-6">
              <input name="education" placeholder="Education" className="form-control" onChange={handleChange} />
            </div>
            <div className="col-12 col-md-6">
              <input name="fatherName" placeholder="Father's Name" className="form-control" onChange={handleChange} />
            </div>
            <div className="col-12 col-md-6">
              <input name="motherName" placeholder="Mother's Name" className="form-control" onChange={handleChange} />
            </div>
            <div className="col-12 col-md-6">
              <input name="siblings" placeholder="No. of Siblings" className="form-control" onChange={handleChange} />
            </div>
            <div className="col-12">
              <label className="form-label">Upload Resume</label>
              <input type="file" accept=".pdf,.doc,.docx" onChange={handleFileChange} className="form-control" />
            </div>
            <div className="col-12">
              <button type="submit" className="btn btn-primary mt-2 w-100">
                {loading ? "Generating..." : "Submit & Generate AI Question"}
              </button>
            </div>
          </form>

          {aiQuestion && (
            <div className="mt-5 p-3 bg-light rounded border">
              <h3 className="h6 fw-bold mb-2">🤖 Question {questionNumber}:</h3>
              <p>{aiQuestion}</p>

              <div className="mt-3">
                <h4 className="h6 fw-bold">🎙️ Your Answer (Voice Input)</h4>
                <p className="text-secondary mb-2">{transcript || "Your speech will appear here..."}</p>
                <div className="btn-group" role="group">
                  <button type="button" onClick={startListening} className="btn btn-success">
                    Start Talking
                  </button>
                  <button type="button" onClick={stopListening} className="btn btn-danger">
                    Stop
                  </button>
                  <button type="button" onClick={resetTranscript} className="btn btn-secondary">
                    Reset
                  </button>
                </div>
              </div>

              {analysis && (
                <div className="mt-4 p-3 bg-warning bg-opacity-25 rounded">
                  <h4 className="h6 fw-bold mb-2">📊 Answer Analysis</h4>
                  <p>Fluency Score: {analysis.fluency}%</p>
                  <p>Grammar Accuracy: {analysis.grammar}%</p>
                  <div className="text-end">
                    <button onClick={handleNextQuestion} className="btn btn-info mt-2">
                      Next Question
                    </button>
                  </div>
                </div>
              )}
            </div>
          )}
        </div>
      </div>
    
  )};
