// src/components/EnglishLearningForm.js
import React, { useState, useEffect, useRef } from "react";
import axios from "axios";
import SpeechRecognition, { useSpeechRecognition } from "react-speech-recognition";
import * as webgazer from "webgazer";

const EnglishLearningForm = () => {
  const [aiQuestion, setAiQuestion] = useState("");
  const [loading, setLoading] = useState(false);
  const [analysis, setAnalysis] = useState(null);
  const [questionNumber, setQuestionNumber] = useState(1);
  const [questionHistory, setQuestionHistory] = useState(new Set());
  const videoRef = useRef(null);

  const {
    transcript,
    listening,
    resetTranscript,
    browserSupportsSpeechRecognition,
  } = useSpeechRecognition();

  const fetchUniqueQuestion = async () => {
    const maxRetries = 5;
    for (let i = 0; i < maxRetries; i++) {
      const response = await axios.get("http://localhost:5000/api/english-learning-question");
      const newQuestion = response.data.question;
      if (!questionHistory.has(newQuestion)) {
        setQuestionHistory((prev) => new Set(prev).add(newQuestion));
        return newQuestion;
      }
    }
    return "Unable to generate a unique question. Please try again later.";
  };

  const handleStart = async () => {
    setLoading(true);
    try {
      const question = await fetchUniqueQuestion();
      setAiQuestion(question);
      resetTranscript();
      setAnalysis(null);
    } catch (error) {
      console.error("Error fetching AI question:", error);
    }
    setLoading(false);
  };

  const startListening = () => {
    resetTranscript();
    SpeechRecognition.startListening({ continuous: true });
  };

  const stopListening = () => {
    SpeechRecognition.stopListening();
  };

  const analyzeTranscript = async () => {
    try {
      const response = await axios.post("http://localhost:5000/api/analyze-answer", { text: transcript });
      setAnalysis(response.data);
    } catch (error) {
      console.error("Error analyzing transcript:", error);
    }
  };

  useEffect(() => {
    if (!listening && transcript) {
      analyzeTranscript();
    }
  }, [listening]);

  const handleNextQuestion = async () => {
    setLoading(true);
    try {
      const question = await fetchUniqueQuestion();
      setAiQuestion(question);
      resetTranscript();
      setAnalysis(null);
      setQuestionNumber((prev) => prev + 1);
    } catch (error) {
      console.error("Error generating next question:", error);
    }
    setLoading(false);
  };

  const startCamera = async () => {
    try {
      const stream = await navigator.mediaDevices.getUserMedia({ video: true });
      if (videoRef.current) {
        videoRef.current.srcObject = stream;
      }
    } catch (err) {
      console.error("Camera access denied or not available", err);
    }
  };

  const startEyeTracking = () => {
    webgazer.setGazeListener((data, elapsedTime) => {
      if (data) {
        console.log("Gaze at:", data.x, data.y);
      }
    }).begin();

    webgazer.showVideo(false).showFaceOverlay(false).showFaceFeedbackBox(false);
  };

  useEffect(() => {
    startCamera();
    startEyeTracking();
  }, []);

  if (!browserSupportsSpeechRecognition) {
    return <span>Your browser does not support speech recognition.</span>;
  }

  return (
    <div className="bg-white shadow-lg rounded-2xl p-6 max-w-2xl mx-auto mt-10">
      <h2 className="text-2xl font-bold mb-4">English Learning Assistant</h2>

      {/* üëÅÔ∏è Eye-Tracking Camera Feed */}
      <div className="mb-4">
        <h4 className="text-lg font-semibold">üì∑ Eye Tracking Camera (Live)</h4>
        <video ref={videoRef} autoPlay playsInline className="w-full max-h-56 rounded border" />
      </div>

      <button
        onClick={handleStart}
        className="bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700 mb-4"
      >
        {loading ? "Generating..." : "Start Practice"}
      </button>

      {aiQuestion && (
        <div className="mt-4 p-4 bg-green-100 rounded-md">
          <h3 className="font-semibold text-lg">üìò Question {questionNumber}:</h3>
          <p>{aiQuestion}</p>

          <div className="mt-4">
            <h4 className="font-semibold">üé§ Your Answer (Voice Input)</h4>
            <p className="text-gray-700 mb-2">{transcript || "Your speech will appear here..."}</p>
            <div className="flex gap-4">
              <button onClick={startListening} className="bg-blue-500 text-white px-3 py-1 rounded hover:bg-blue-600">
                Start Talking
              </button>
              <button onClick={stopListening} className="bg-red-500 text-white px-3 py-1 rounded hover:bg-red-600">
                Stop
              </button>
              <button onClick={resetTranscript} className="bg-gray-300 px-3 py-1 rounded hover:bg-gray-400">
                Reset
              </button>
            </div>
          </div>

          {analysis && (
            <div className="mt-6 p-4 bg-yellow-100 rounded-md">
              <h4 className="font-semibold text-lg">üìä Answer Analysis</h4>
              <p>Fluency Score: {analysis.fluency}%</p>
              <p>Grammar Accuracy: {analysis.grammar}%</p>
              <p className="mt-2 text-right">
                <button onClick={handleNextQuestion} className="mt-2 bg-indigo-600 text-white px-4 py-2 rounded hover:bg-indigo-700">
                  Next Question
                </button>
              </p>
            </div>
          )}
        </div>
      )}
    </div>
  );
};

export default EnglishLearningForm;
