// src/components/EngineeringForm.js
import React, { useState, useEffect } from "react";
import axios from "axios";
import SpeechRecognition, { useSpeechRecognition } from "react-speech-recognition";

const Engineeringsuggest = () => {
  const [formData, setFormData] = useState({
    name: "",
    location: "",
    education: "",
    fatherName: "",
    motherName: "",
    siblings: "",
    resume: null,
  });

  const [aiQuestion, setAiQuestion] = useState("");
  const [loading, setLoading] = useState(false);
  const [analysis, setAnalysis] = useState(null);

  const {
    transcript,
    listening,
    resetTranscript,
    browserSupportsSpeechRecognition,
  } = useSpeechRecognition();

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleFileChange = (e) => {
    setFormData({ ...formData, resume: e.target.files[0] });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    try {
      const formDataToSend = new FormData();
      Object.entries(formData).forEach(([key, value]) => {
        formDataToSend.append(key, value);
      });

      const response = await axios.post("http://localhost:5000/api/engineering-question", formDataToSend);
      setAiQuestion(response.data.question);
    } catch (error) {
      console.error("Error sending data to GPT API:", error);
    }
    setLoading(false);
  };

  const startListening = () => {
    resetTranscript();
    SpeechRecognition.startListening({ continuous: true });
  };

  const stopListening = () => {
    SpeechRecognition.stopListening();
  };

  const analyzeTranscript = async () => {
    try {
      const response = await axios.post("http://localhost:5000/api/analyze-answer", { text: transcript });
      setAnalysis(response.data);
    } catch (error) {
      console.error("Error analyzing transcript:", error);
    }
  };

  useEffect(() => {
    if (!listening && transcript) {
      analyzeTranscript();
    }
  }, [listening]);

  if (!browserSupportsSpeechRecognition) {
    return <span>Your browser does not support speech recognition.</span>;
  }

  return (
    <div className="bg-white shadow-lg rounded-2xl p-6 max-w-2xl mx-auto mt-10">
      <h2 className="text-2xl font-bold mb-4">Engineering Interview Setup</h2>
      <form onSubmit={handleSubmit} className="grid grid-cols-1 gap-4">
        <input name="name" placeholder="Your Name" className="input" onChange={handleChange} />
        <input name="location" placeholder="Location" className="input" onChange={handleChange} />
        <input name="education" placeholder="Education" className="input" onChange={handleChange} />
        <input name="fatherName" placeholder="Father's Name" className="input" onChange={handleChange} />
        <input name="motherName" placeholder="Mother's Name" className="input" onChange={handleChange} />
        <input name="siblings" placeholder="No. of Siblings" className="input" onChange={handleChange} />

        <div>
          <label className="block mb-1">Upload Resume</label>
          <input type="file" accept=".pdf,.doc,.docx" onChange={handleFileChange} />
        </div>

        <button type="submit" className="bg-blue-600 text-white px-4 py-2 rounded mt-4 hover:bg-blue-700">
          {loading ? "Generating..." : "Submit & Generate AI Question"}
        </button>
      </form>

      {aiQuestion && (
        <div className="mt-6 p-4 bg-green-100 rounded-md">
          <h3 className="font-semibold text-lg">🤖 AI Generated Question:</h3>
          <p>{aiQuestion}</p>

          <div className="mt-4">
            <h4 className="font-semibold">🎙️ Your Answer (Voice Input)</h4>
            <p className="text-gray-700 mb-2">{transcript || "Your speech will appear here..."}</p>
            <div className="flex gap-4">
              <button onClick={startListening} className="bg-blue-500 text-white px-3 py-1 rounded hover:bg-blue-600">
                Start Talking
              </button>
              <button onClick={stopListening} className="bg-red-500 text-white px-3 py-1 rounded hover:bg-red-600">
                Stop
              </button>
              <button onClick={resetTranscript} className="bg-gray-300 px-3 py-1 rounded hover:bg-gray-400">
                Reset
              </button>
            </div>
          </div>

          {analysis && (
            <div className="mt-6 p-4 bg-yellow-100 rounded-md">
              <h4 className="font-semibold text-lg">📊 Answer Analysis</h4>
              <p>Fluency Score: {analysis.fluency}%</p>
              <p>Grammar Accuracy: {analysis.grammar}%</p>
              <p className="mt-2 text-right">
                <button className="mt-2 bg-indigo-600 text-white px-4 py-2 rounded hover:bg-indigo-700">
                  Next Question
                </button>
              </p>
            </div>
          )}
        </div>
      )}
    </div>
  );
};

export default Engineeringsuggest;

