import React, { useState, useEffect } from "react";
import { auth, db } from "../firebase";
import { doc, setDoc, getDoc } from "firebase/firestore";
import "../Loginstyle.css";
import {
  createUserWithEmailAndPassword,
  signInWithEmailAndPassword,
  sendEmailVerification,
  signOut
} from "firebase/auth";
import { useNavigate } from "react-router-dom";

const Login = () => {
  const navigate = useNavigate();
  const [isLogin, setIsLogin] = useState(true); // Default to login tab
  const [signupData, setSignupData] = useState({ email: "", password: "" });
  const [loginData, setLoginData] = useState({ email: "", password: "" });
  const [message, setMessage] = useState("");

  useEffect(() => {
    if (message) {
      alert(message);
      setMessage("");
    }
  }, [message]);

  const handleSignup = async () => {
    if (!signupData.email || !signupData.password) {
      setMessage("Email and password are required for signup.");
      return;
    }

    try {
      // Create user
      const userCredential = await createUserWithEmailAndPassword(
        auth,
        signupData.email,
        signupData.password
      );
      const user = userCredential.user;

      // Send verification email
      await sendEmailVerification(user);
      setMessage("Verification email sent. Please check your inbox.");

      // Store basic user data immediately (before verification)
      await setDoc(doc(db, "users", user.uid), {
        email: user.email,
        createdAt: new Date(),
        emailVerified: false // Track verification status
      });

      // Sign out and wait for verification
      await signOut(auth);

      // Check verification status periodically
      const interval = setInterval(async () => {
        await user.reload();
        if (user.emailVerified) {
          clearInterval(interval);
          
          // Update verification status in Firestore
          await setDoc(doc(db, "users", user.uid), {
            emailVerified: true
          }, { merge: true });

          alert("Email verified! Please login.");
          setIsLogin(true);
        }
      }, 5000);

      return () => clearInterval(interval);

    } catch (error) {
      if (error.code === "auth/email-already-in-use") {
        setMessage("Email already registered. Please login instead.");
        setIsLogin(true);
      } else {
        setMessage(`Signup error: ${error.message}`);
      }
    }
  };

  const handleLogin = async () => {
    if (!loginData.email || !loginData.password) {
      setMessage("Email and password are required.");
      return;
    }

    try {
      // Authenticate user
      const userCredential = await signInWithEmailAndPassword(
        auth,
        loginData.email,
        loginData.password
      );
      const user = userCredential.user;

      // Check Firestore for user record
      const userDoc = await getDoc(doc(db, "users", user.uid));

      if (!userDoc.exists()) {
        // User doesn't exist in Firestore (legacy account)
        await setDoc(doc(db, "users", user.uid), {
          email: user.email,
          createdAt: new Date(),
          emailVerified: user.emailVerified
        });
      }

      // For existing Firestore users, skip verification check
      if (userDoc.exists() && userDoc.data().emailVerified) {
        navigate("/home");
        return;
      }

      // For new accounts or unverified users
      if (!user.emailVerified) {
        await sendEmailVerification(user);
        setMessage("Please verify your email first. New link sent.");
        await signOut(auth);
        return;
      }

      // If verified but not in Firestore (shouldn't happen)
      navigate("/home");

    } catch (error) {
      if (error.code === "auth/user-not-found") {
        setMessage("Account not found. Please sign up.");
        setIsLogin(false);
      } else if (error.code === "auth/wrong-password") {
        setMessage("Incorrect password.");
      } else {
        setMessage(`Login error: ${error.message}`);
      }
    }
  };

  return (
    <div className="form-structor">
      {/* Signup Form */}
      <div className={`signup ${isLogin ? "slide-up" : ""}`}>
        <h2 className="form-title" onClick={() => setIsLogin(false)}>
          <span>or</span>Sign up
        </h2>
        <div className="form-holder">
          <input
            type="email"
            className="input"
            placeholder="Email"
            value={signupData.email}
            onChange={(e) => setSignupData({...signupData, email: e.target.value})}
          />
          <input
            type="password"
            className="input"
            placeholder="Password"
            value={signupData.password}
            onChange={(e) => setSignupData({...signupData, password: e.target.value})}
          />
        </div>
        <button className="submit-btn" onClick={handleSignup}>
          Sign up
        </button>
      </div>

      {/* Login Form */}
      <div className={`login ${isLogin ? "" : "slide-up"}`}>
        <div className="center">
          <h2 className="form-title" onClick={() => setIsLogin(true)}>
            <span>or</span>Log in
          </h2>
          <div className="form-holder">
            <input
              type="email"
              className="input"
              placeholder="Email"
              value={loginData.email}
              onChange={(e) => setLoginData({...loginData, email: e.target.value})}
            />
            <input
              type="password"
              className="input"
              placeholder="Password"
              value={loginData.password}
              onChange={(e) => setLoginData({...loginData, password: e.target.value})}
            />
          </div>
          <button className="submit-btn" onClick={handleLogin}>
            Log in
          </button>
        </div>
      </div>
    </div>
  );
};

export default Login;
