import React, { useState, useEffect } from "react";
import { auth,db } from "../firebase";
import { doc, setDoc,getDoc } from "firebase/firestore";
import "../Loginstyle.css"
import {
  createUserWithEmailAndPassword,
  signInWithEmailAndPassword,
  sendEmailVerification,
  signOut
} from "firebase/auth";
 
import GoogleLogin from "./GoogleLogin";
import GithubLogin from "./GithubLogin";

const Login=()=> {
  const [isLogin, setIsLogin] = useState(false);
  const [formData, setFormData] = useState({
    
    email: "",
    password: "",
  });
  const [message, setMessage] = useState("");
useEffect(() => {
  if (message) {
    alert(message);
    setMessage(""); 
  }
}, [message]);
  const handleInputChange = (e) => {
    setFormData((prev) => ({
      ...prev,
      [e.target.name]: e.target.value,
    }));
  };

  const handleSignup = async () => {
    if (!formData.email || !formData.password) {
    setMessage(" Email and password are required for signup.");
    return;
  }
    try {
      const userCredential=await createUserWithEmailAndPassword(auth, formData.email, formData.password);
      const user = userCredential.user;
      await sendEmailVerification(user);

      await setDoc(doc(db, "users", user.uid), {
      email: user.email,
      createdAt: new Date()
    });

    setMessage("✅ Signup successful! Please check your email to verify your account.");;
    } catch (error) {
      if (error.code === "auth/email-already-in-use") {
      setMessage(" Email already in use. Please login instead.");
    } else {
      setMessage(` ${error.message}`);
    }
    }
  };

  const handleLogin = async () => {
    if (!formData.email || !formData.password) {
    setMessage(" Email and password are required for login.");
    return;
  }
    try {
      const userCredential=await signInWithEmailAndPassword(auth, formData.email, formData.password);
      const user = userCredential.user;
      const userRef = doc(db, "users", user.uid);
    const userSnap = await getDoc(userRef);

    if (!userSnap.exists()) {
      
      await signOut(auth);
      setMessage("User not found. Please sign up first.");
      return;
    }
      setMessage(" Login successful!");
    } catch (error) {
     
      setMessage(` ${error.message}`);
    
    }
  };

  return (
    <div className="form-structor">
      
      <div className={`signup ${isLogin ? "slide-up" : ""}`}>
        <h2 className="form-title" onClick={() => setIsLogin(false)}>
          <span>or</span>Sign up
        </h2>
        <div className="form-holder">
          
          <input
            type="email"
            className="input"
            placeholder="Email"
            name="email"
            onChange={handleInputChange}
          />
          <input
            type="password"
            className="input"
            placeholder="Password"
            name="password"
            onChange={handleInputChange}
          />
        </div>
        <button className="submit-btn" onClick={handleSignup}>
          Sign up
        </button>
      </div>

      
      <div className={`login ${isLogin ? "" : "slide-up"}`}>
        <div className="center">
          <h2 className="form-title" onClick={() => setIsLogin(true)}>
            <span>or</span>Log in
          </h2>
          
          <div className="form-holder">
            <div className="d-flex justify-content-center align-items-center mt-2">
  <div>
    <GoogleLogin />
    <GithubLogin />
  </div>
</div>
            <div className="d-flex justify-content-center">
  <h4>or</h4>
</div>
            <input
              type="email"
              className="input"
              placeholder="Email"
              name="email"
              onChange={handleInputChange}
            />
            <input
              type="password"
              className="input"
              placeholder="Password"
              name="password"
              onChange={handleInputChange}
            />
          </div>
          <button className="submit-btn text-white " onClick={handleLogin}>
            Log in
          </button>
        </div>
      </div>

     
     
    </div>
  );
}
export default Login;
