import React, { useState, useEffect } from "react";
import { auth } from "../firebase";
import "../Loginstyle.css"
import {
  createUserWithEmailAndPassword,
  signInWithEmailAndPassword,
} from "firebase/auth";
 
import GoogleLogin from "../GoogleLogin";
import GithubLogin from "./GithubLogin";

const Login=()=> {
  const [isLogin, setIsLogin] = useState(false);
  const [formData, setFormData] = useState({
    
    email: "",
    password: "",
  });
  const [message, setMessage] = useState("");

  const handleInputChange = (e) => {
    setFormData((prev) => ({
      ...prev,
      [e.target.name]: e.target.value,
    }));
  };

  const handleSignup = async () => {
    try {
      await createUserWithEmailAndPassword(auth, formData.email, formData.password);
      setMessage("✅ Signup successful!");
    } catch (error) {
      setMessage(`❌ ${error.message}`);
    }
  };

  const handleLogin = async () => {
    try {
      await signInWithEmailAndPassword(auth, formData.email, formData.password);
      setMessage("✅ Login successful!");
    } catch (error) {
      setMessage(
        error.code === "auth/user-not-found"
          ? "❌ User not found. Please sign up first."
          : `❌ ${error.message}`
      );
    }
  };

  return (
    <div className="form-structor">
      
      <div className={`signup ${isLogin ? "slide-up" : ""}`}>
        <h2 className="form-title" onClick={() => setIsLogin(false)}>
          <span>or</span>Sign up
        </h2>
        <div className="form-holder">
          
          <input
            type="email"
            className="input"
            placeholder="Email"
            name="email"
            onChange={handleInputChange}
          />
          <input
            type="password"
            className="input"
            placeholder="Password"
            name="password"
            onChange={handleInputChange}
          />
        </div>
        <button className="submit-btn" onClick={handleSignup}>
          Sign up
        </button>
      </div>

      
      <div className={`login ${isLogin ? "" : "slide-up"}`}>
        <div className="center">
          <h2 className="form-title" onClick={() => setIsLogin(true)}>
            <span>or</span>Log in
          </h2>
          <div className="form-holder">
            <GoogleLogin/>
            <h1>or</h1>
            <GithubLogin/>
            <input
              type="email"
              className="input"
              placeholder="Email"
              name="email"
              onChange={handleInputChange}
            />
            <input
              type="password"
              className="input"
              placeholder="Password"
              name="password"
              onChange={handleInputChange}
            />
          </div>
          <button className="submit-btn" onClick={handleLogin}>
            Log in
          </button>
        </div>
      </div>

     
      {message && (
        <div style={{ marginTop: "10px", textAlign: "center", color: "darkblue" }}>
          <strong>{message}</strong>
        </div>
      )}
    </div>
  );
}
export default Login;
