import React, { useState, useEffect } from "react";
import { auth } from "../firebase";
import "../Loginstyle.css"
import {
  createUserWithEmailAndPassword,
  signInWithEmailAndPassword,
} from "firebase/auth";
 
import GoogleLogin from "./GoogleLogin";
import GithubLogin from "./GithubLogin";

const Login=()=> {
  const [isLogin, setIsLogin] = useState(false);
  const [formData, setFormData] = useState({
    
    email: "",
    password: "",
  });
  const [message, setMessage] = useState("");
useEffect(() => {
  if (message) {
    alert(message);
    setMessage(""); 
  }
}, [message]);
  const handleInputChange = (e) => {
    setFormData((prev) => ({
      ...prev,
      [e.target.name]: e.target.value,
    }));
  };

  const handleSignup = async () => {
    try {
      await createUserWithEmailAndPassword(auth, formData.email, formData.password);
      setMessage("✅ Signup successful!");
    } catch (error) {
      setMessage(`❌ ${error.message}`);
    }
  };

  const handleLogin = async () => {
    try {
      await signInWithEmailAndPassword(auth, formData.email, formData.password);
      setMessage("✅ Login successful!");
    } catch (error) {
      setMessage(
        
          " User not found. Please sign up first."
          
      );
    }
  };

  return (
    <div className="form-structor">
      
      <div className={`signup ${isLogin ? "slide-up" : ""}`}>
        <h2 className="form-title" onClick={() => setIsLogin(false)}>
          <span>or</span>Sign up
        </h2>
        <div className="form-holder">
          
          <input
            type="email"
            className="input"
            placeholder="Email"
            name="email"
            onChange={handleInputChange}
          />
          <input
            type="password"
            className="input"
            placeholder="Password"
            name="password"
            onChange={handleInputChange}
          />
        </div>
        <button className="submit-btn" onClick={handleSignup}>
          Sign up
        </button>
      </div>

      
      <div className={`login ${isLogin ? "" : "slide-up"}`}>
        <div className="center">
          <h2 className="form-title" onClick={() => setIsLogin(true)}>
            <span>or</span>Log in
          </h2>
          
          <div className="form-holder">
            <div className="d-flex justify-content-center align-items-center mt-2">
  <div>
    <GoogleLogin />
    <GithubLogin />
  </div>
</div>
            <div className="d-flex justify-content-center">
  <h4>or</h4>
</div>
            <input
              type="email"
              className="input"
              placeholder="Email"
              name="email"
              onChange={handleInputChange}
            />
            <input
              type="password"
              className="input"
              placeholder="Password"
              name="password"
              onChange={handleInputChange}
            />
          </div>
          <button className="submit-btn text-white " onClick={handleLogin}>
            Log in
          </button>
        </div>
      </div>

     
      {message && (
        alert(message)
      )}
    </div>
  );
}
export default Login;
